//program 1
runAgain = true;
while(runAgain){
    let age = Number.parseInt(prompt("enter age"))
    if(age>=18){
        alert("yes you can drive")
    }
    else if(age>0&&age<18){
        alert("no you cant drive")
    }
    else{
         console.error("this value is negative")
    }

    runAgain = confirm("do you want to play again?")
}
